    　　Sleep(5000);/*这里等待5秒,告诉你程序运行到什么地方*/ 
    　　scanf("%s",str1);/*这两句无需你再输入,是对stdin流再扫描 */ 
    　　scanf("%s",str2);/*这两句无需你再输入,是对stdin流再扫描 */
